function appendCss(url, where = "@head") {
	let x = document.createElement("link");
	x.href = url;
	x.rel="stylesheet";
	x.type = "text/css";
	return get(where).appendChild(x);
}
/* 動態載入 CSS*/



function gFonts(fonts, effect="") {
	// googleFonts("字體1|字體2", "特效1|特效2"
	let x = document.createElement("link");
	if(effect!=""){effect= "&effect="+effect;}
	let url = "https://fonts.googleapis.com/css?family=" + fonts + effect;
	x.href = url.trim();
	x.rel="stylesheet";
	x.type = "text/css";
	return get("@head").appendChild(x);
}
/* 動態載入 Google Fonts CSS*/

function gIcons(effect="") {
	// googleFonts("字體1|字體2", "特效1|特效2"
	let x = document.createElement("link");
	if(effect!=""){effect= "&effect="+effect;}
	let url = "https://fonts.googleapis.com/icon?family=Material+Icons" + effect;
	x.href = url.trim();
	x.rel="stylesheet";
	x.type = "text/css";
	return get("@head").appendChild(x);
}
