function sortACBD(arr){ //陣列切半，前後穿插 123456 => 142536;
	arr = arr.slice(0);
	let i = 0;
	let len = arr.length;
	let shang = parseInt(len / 2);
	let yu = len % 2;
	let newArr = [];
	while (i < shang){
		newArr.push(arr[i]);
		newArr.push(arr[i+shang])
		i++;
	} 
	if(yu == 1){newArr.push(arr[len-1])};
  return newArr;
}


