String.prototype.slicePro = function(begin, end) {
	// "ABC字串".slicePro(2) => C字串;
	// "ABC字串".slicePro(0 , 2) => AB;
	// "ABC字串".slicePro(-2) => 字串;
	// "ABC字串".slicePro("2") => C (陣列的2);
	// "ABC字串".slicePro("0, -1, 2") => A串C (陣列位);
	// "ABC字串".slicePro("length") => 5 (字數);
	return get_txt(this , begin, end);
}
function get_txt(txt , begin, end, newTxt) {
    let arr = Array.from(txt);
	let len = arr.length;
	let brr = [];
	let x = "";
	if(typeof(begin) == "string"){
		if(/,/.test(begin)){
			brr = begin.replace(/ /g, "").split(",");
			brr = brr.map( (e) => Number(e));
			let ben = brr.length;
			for(let i = 0; i < ben; i++){
				let n = brr[i];
				if(n>=0){
					x += arr.slice(n, n+1).join("");
				}else if(n <0){
					n = len + n;
					x += arr.slice(n, n+1).join("");
				}
			}
			return x;
		}else if(begin.toLowerCase() == "length" || begin.toLowerCase() == "leng" || begin.toLowerCase() == "len"){
			return len;
		}else{
			let n = Number(begin);
			return arr.slice(n, n+1).join("");	
		}	
	}else{
		return arr.slice(begin, end).join("");
	}
}
