function get(e, i = 0) {
	// 以 get("物件 數字") 為標準， get("物件", 數字) 為其次;
	// get("#id") 或 get("@tag") 或 get("@tag", "all") 或 get("@tag", 2) 或 get("$div", "all") 或 get("$div.class", "all")或 get("$div.classA, div.classB", "all")
	// e 必須是 #id 或 @tag 或 %name 或 .className 或 $querySelector;
	// get("@div|1") 等於 get("@div, 1")  等於 get("@div 1") 等於 get("@div", 1) 用 直豎 | 或逗號 或空格 分開。;

	if(typeof(e) == "object"){
		//返回未見本身 this;
		return e;	
	}else if(e == "this"){
		// this 不可用字串;
		return ;
	}else if(typeof(e) == "string"){
		let sp = /[,|]\s+|[,|]|\s+/;
		// 可用逗號、直數、空格分開;
		if(e.split(sp).length == 2 ){
			i = e.split(sp)[1];
			e = e.split(sp)[0];
		}
		let x = e.slice(1);

		if(/^(\#)(\w+)/.test(e)){
			return document.getElementById(x);
		}else if(/^(\@)(\w+)/.test(e)){
			if(i == "all"){
				return document.getElementsByTagName(x);
			}else{
				return document.getElementsByTagName(x)[i];	
			}
		}else if(/^(\.)(\w+)/.test(e)){	
			
			if(i == "all"){			
				return document.getElementsByClassName(x);
			}else if (i > 0){
				return document.getElementsByClassName(x)[i];	
			}else{
				return document.getElementsByClassName(x)[0];		
			}
		}else if(/^(\$)(\w+)/.test(e)){
			if(i == "all"){
				return document.querySelectorAll(x);
			}else if (i > 0){
				return document.querySelectorAll(x)[i];
			}else{
				return document.querySelector(x);		
			}
		}else if(/^(\%)(\w+)/.test(e)){
			if(i == "all"){
				return document.getElementsByName(x);
			}else if (i >= 0){
				return document.getElementsByName(x)[i];		
			}
		}
	}
}/* 取得物件*/


