// .js自動增加 <script> ;
function append(what, where = "@body", ele = "div") {
	return add(what, where, ele, how = "append");
}
function insertBefore(what, where = "@body", ele = "div") {
	return add(what, where, ele, how = "insertBefore");
}

function add(what, where = "@body", ele = "div", how = "append", id = "", cssclass = "", cssstyle = ""){
	if(where == ""){where = "@body";}
	if(ele == ""){ele = "div";}
	let x;
	if(/^([@#$%.])(\w+)/.test(what)){
		x = get(what);
	}else if( what.slicePro(-3)== ".js" && where == "@head"){
		// 在 <head> 增加 <script>，語法 add("xxx.js", "@head");
		x = document.createElement("script");
		x.onload = function () {}
		// 載入完成，執行fn;
		x.src = what;	
	}else{
		x = document.createElement(ele);
		if( what.slicePro(-3)== ".js" && where != "@head"){
			// 在 <body> 增加 <script>，語法 add("xxx.js");
			s = document.createElement("script");
			s.onload = function () {}
			// 載入完成，執行fn;
			s.src = what;			
			x.appendChild(s);
		}else{
			x.innerHTML = what;			
		}
		x.id = id;
		x.class= cssclass;
		x.style = cssstyle;
	}

	if(how == "before" || how == "home" || how == "insertBefore" || how == 0){
		return get(where).insertBefore(x, get(where).firstChild);	
	}else{
		return get(where).appendChild(x);
	}	
}

