function random(m, n = 0) {
	// 亂數取數 數字 (m最大, n最小);
	// 亂數排序 字串、陣列;
	let t = typeof(m);
	if( t == "number"){
		return mathRandom(n, m);
	}else if (t == "string"){
		return set_randomTxt(m);
	}else if (t == "object"){
		return m.sortRand();	
	}	
} 
/* 設定亂數取數；字串亂數、陣列排序;	 */

function randomNum(m, n = 0) {
	// 隨機0~3的整數 randomNum(3);
	// 隨機9~4以內的數 randomNum(9, 4)
	return mathRandom(n, m);
} 
function mathRandom(n, m) {
	// 隨機0~3的整數 mathRandom(0, 3);
	// 隨機4~9以內的數 mathRandom(4, 9)
    var num = Math.floor(Math.random() * (m - n + 1) + n)
    return num
} /* 範圍內隨機整數數字 */




