// 在網頁右上角計算時間;
function timedCount() {
	let divT = get_id("divT");
    divT.style.cssText = "border-radius:0px 0px 0px 10px; box-shadow: 0 0px #eee; background:#ccc; padding:1px 1px 1px 10px; position:absolute; width:80px; height:30px; z-index:1; display:none;overflow: auto; right:0px; top:0px;";
    divT.style.display = "";
    if(timeNow >= 0 && timeNow <= 59){
	  divT.innerHTML = "0分 " + timeNow + "秒";
    }else if(timeNow >= 60){		
	  divT.innerHTML = parseInt(timeNow/60) + "分 " +  (timeNow%60) + "秒";
	}

  timeNow = timeNow + 1;
  timeSet = setTimeout(timedCount, 1000);
}

function startCountTime() {
  if (!timer_is_on) {
    timer_is_on = 1;
    timedCount();
  }
}

function stopCountTime() {
  clearTimeout(timeSet);
  timer_is_on = 0;
}
