function getSelText() {
	//在頁面選取的字串是;
	let x;
	if (window.getSelection) {
		x = window.getSelection();
	} else if (document.getSelection) {
		x = document.getSelection();
	} else if (document.selection) {
		x = document.selection.createRange().text;
	} else return;
	let t = x.toString();
	if(t != ""){ 
		return t;
	} else {
		return false;	
	}
} 
