// ==S四縣客語詞彙變調=========================================;
function gongHakkaS(t) {
    // 調號轉字母調;
    t = t.replace(/([a-z]{0,4})([mngbdgaeiou])(ˊ)/gi, '$1$2z');
    t = t.replace(/([a-z]{0,4})([mngbdgaeiou])(ˇ)/gi, '$1$2v');
    t = t.replace(/([a-z]{0,4})([mngbdgaeiou])(ˋ)/gi, '$1$2s');
    t = t.replace(/([a-z]{0,4})([mngbdgaeiou])(ˉ)/gi, '$1$2c');

    t = t.replace(/([a-z]{0,4})([mngbdgaeiou])( ́)/gi, '$1$2z');
    t = t.replace(/([a-z]{0,4})([mngbdgaeiou])( ̌)/gi, '$1$2v');
    t = t.replace(/([a-z]{0,4})([mngbdgaeiou])( ̀)/gi, '$1$2s');
    t = t.replace(/([a-z]{0,4})([mngbdgaeiou])( ̄)/gi, '$1$2c');

    // 四字變調;
    //用分隔成x-xx-x;
    t = t.replace(/([a-z])(-|_| )([a-z])(-|_| )([a-z])(-|_| )([a-z])(\b)/gi, '$1$2$3$4$5$6$7$8');

    // 三疊字z不變調;
    t = t.replace(/([a-z]{1,6})(z)(-|_| )(\1)(z)(-|_| )(\1)(z)(\b)/gi, '$1ˊ$3$4ˊ$6$7ˊ$9');
    // 三疊字v變調;
    t = t.replace(/([a-z]{1,6})(v)(-|_| )(\1)(v)(-|_| )(\1)(v)(\b)/gi, '$1ˊ$3$4$5$6$7$8$9');
    // z二字變調;
    t = t.replace(/([aeiouymng])(z)(-|_| )(zh|ch|sh|rh|ng|b|p|m|f|v|d|t|n|l|g|k|h|z|c|s|j|q|x|r{0,1})([aeioumngbd]{1,5})([z]{0,1})(\b)/gi, '$1ˇ$3$4$5$6$7');

    // e變調;
    t = t.replace(/([aeiouymngbd])(s)(-|_| )()(e)(s)(\b)/gi, '$1$2$3$4$5ˇ$7');

    return t;
}
