function arr2json(arr, brr = arr){
	let len = arr.length;
	let obj = {};
	let newArr = [];
	let y = 0;
	if(arr == brr){y = 1;}

	for(let i = 0; i < len; i+= 1+y){
		obj = {};
		obj.A = arr[i];
		obj.B = brr[i+y];
		newArr.push(obj);
	}
	return newArr;
}







function sortJson(obj, who, how = "<"){
	// who 可填 A 或 B 或 A.length 或 B.length;
	let ob = obj.slice(0); 
	// 複製一個新的，不改動原始陣列;
	who = who.toLowerCase();

	if(how == "<"){
		if(who == "a"){
			return ob.sort(function(a,b){
				return a.A - b.A;
			});
		}else if(who == "b"){
			return ob.sort(function(a,b){
				return a.B - b.B;
			});
		}else if(who.slice(0,5) == "a.len"){
			return ob.sort(function(a,b){
				return a.A.length - b.A.length;
			});
		}else if(who.slice(0,5) == "b.len"){
			ob = ob.sort(function(a,b){
				return a.B.length - b.B.length;
			});
		}
	}else{
		if(who == "a"){
			return ob.sort(function(b,a){
				return a.A - b.A;
			});
		}else if(who == "b"){
			return ob.sort(function(b,a){
				return a.B - b.B;
			});
		}else if(who.slice(0,5) == "a.len"){
			return ob.sort(function(b,a){
				return a.A.length - b.A.length;
			});
		}else if(who.slice(0,5) == "b.len"){
			ob = ob.sort(function(b,a){
				return a.B.length - b.B.length;
			});
		}
	
	}
	return ob;
}



